mapper.select:
  		SELECT * FROM ( 
            SELECT rownum rnum, B.* FROM (
		         SELECT * FROM jobopenbasic
                ORDER BY jobopenno DESC 
            ) B 
        ) R WHERE R.rnum BETWEEN #{startNo} AND #{endNo } ORDER BY rnum

SELECT COUNT(*) CNT FROM (
 SELECT * FROM jobopenbasic
) CNT
