mapper.update:
UPDATE qna
SET title=#{title}, pw=#{pw}, content=#{content}
WHERE qnaNo=#{qnaNo}

UPDATE qna
SET asw_code = #{asw_code}, asw_writer = #{asw_writer}, asw_content = #{asw_content}, asw_date= sysdate
WHERE qnaNo = #{qnaNo}

UPDATE qna
SET	asw_code= null, asw_writer = null, asw_content = null, asw_date = null
WHERE qnaNo = #{qnaNo}

UPDATE qna
SET hit = hit+1
WHERE qnaNo = #{qnaNo }
